INSERT INTO [dbo].[Movies] ([Id], [Title], [Director], [ReleaseYear], [Price], [ImgUrl], [Description]) VALUES (5, N'The Shawshank Redemption', N' Frank Darabont', N'1994', CAST(199.00 AS Decimal(18, 2)), N'Images/05  The Shawshank Redemption.jpg', N'Internationally acclaimed actors Tim Robbins and Morgan Freeman star in a compelling drama of hope, friendship and atonement behind the walls of a maximum security prison in The Shawshank Redemption. From a novella by best-selling author Stephen King comes a poignant tale of the human spirit.')
